import CardProductReview from "@/components/Components/Card/CardProductReview";
import BlockMixin from "@/components/Mixins/BlockMixin";
import FormMixin from "@/components/Mixins/FormMixin";
import HeaderMixin from "@/components/Mixins/HeaderMixin";
import FormCustomMixin from "@/components/Components/Mixin/FormCustomMixin";
export default {
  name: 'BlockInquiryReg',
  components: {
    CardProductReview: CardProductReview
  },
  mixins: [BlockMixin, FormMixin, HeaderMixin, FormCustomMixin],
  props: {},
  data: function data() {
    return {
      "formData": [{
        "name": "제목",
        "field": "title",
        "label": "제목",
        "value": "",
        "format": "CInput_text",
        "validate": {
          "num": true,
          "alpha_g": true,
          "alpha_s": true,
          "special": true,
          "required": true,
          "max_length": 30,
          "min_length": 1,
          "valid_required": true
        },
        "placeholder": "제목을 입력하세요"
      }, {
        "name": "내용",
        "field": "question_content",
        "label": "내용",
        "value": "",
        "format": "CInput_textarea",
        "validate": {
          "num": true,
          "alpha_g": true,
          "alpha_s": true,
          "special": true,
          "required": true,
          "max_length": 500,
          "min_length": 1,
          "valid_required": true
        },
        "placeholder": "내용을 입력하세요"
      }, {
        "name": "비밀글 ",
        "field": "is_secret",
        "label": "비밀글로 작성하기",
        "value": false,
        "format": "CInput_checkbox",
        "validate": {
          "num": true,
          "alpha_g": true,
          "alpha_s": true,
          "special": true,
          "required": false,
          "valid_required": false
        },
        "placeholder": ""
      }],
      "inquiry_id": undefined,
      "product_id": undefined
    };
  },
  created: function created() {
    this.inquiry_id = this.$route.query.inquiry_id;
    this.product_id = this.$route.query.product_id;
    this.getInquiryDetail();
    this.initForm();
  },
  methods: {
    initForm: function initForm() {
      this.formData = this.setForm(this.formData);
      var title = find(this.formData, 'field', 'title');
      title.inputCustom = this.inputCustom;
      var content = find(this.formData, 'field', 'question_content');
      content.inputCustom = this.inputCustomTextarea;
    },
    save: function save() {
      var _this = this;

      var result = this.getParamData(this.formData);

      if (result.enable) {
        if (this.inquiry_id === undefined) {
          this.$axios.post("user/".concat(this.user.user_id, "/product/").concat(this.product_id, "/inquiry"), result.params).then(function (res) {
            if (res.status === 200) {
              _this.toast('문의글이 작성되었습니다.');

              _this.$router.replace("/product_detail?id=".concat(_this.product_id, "&tab=2"));
            }
          }).catch(function (err) {
            if (!err.response.data.message) _this.toast(err.response.data.detail);else _this.toast(err.response.data.message);
          });
        } else {
          this.$axios.patch("user/".concat(this.user.user_id, "/product/").concat(this.product_id, "/inquiry/").concat(this.inquiry_id), result.params).then(function (res) {
            if (res.status === 200) {
              _this.toast('문의글이 수정되었습니다.');

              _this.$router.replace("/product_detail?id=".concat(_this.product_id, "&tab=2"));
            }
          }).catch(function (err) {
            if (!err.response.data.message) _this.toast(err.response.data.detail);else _this.toast(err.response.data.message);
          });
        }
      }
    },
    getInquiryDetail: function getInquiryDetail() {
      var _this2 = this;

      if (this.inquiry_id !== undefined) {
        var url = "user/".concat(this.user.user_id, "/product/").concat(this.product_id, "/inquiry/").concat(this.inquiry_id, "?secret=true");
        this.$axios.get(url).then(function (res) {
          var data = res.data;

          _this2.formData.forEach(function (item) {
            item.value = data[item.field];
          });
        });
      }
    }
  },
  computed: {
    containerStyle: function containerStyle() {
      var deco = this.brandBgColor.white;
      return deco;
    },
    buttonCustomCancel: function buttonCustomCancel() {
      var styles = this.cloneItem(this.buttonCustom);
      styles.backgroundColor = this.brandColor.sub.color;
      return styles;
    },
    inputCustomTextarea: function inputCustomTextarea() {
      var styles = this.cloneItem(this.inputCustom);
      styles.height = '160px';
      return styles;
    }
  },
  watch: {}
};
