export default {
  name: "BtnOrdering",
  props: {
    product: {
      type: Object,
      required: true
    },
    width: {
      type: String,
      default: "100%",
      required: false
    },
    height: {
      type: String,
      default: "48px",
      required: false
    },
    styles: {
      type: String,
      default: "more",
      required: false
    },
    name: {
      type: String
    }
  },
  computed: {
    componentName: function componentName() {
      var name = 'btn-';

      switch (this.product.order_conversion_type) {
        case 1:
          name += 'call';
          break;

        case 2:
          name += 'outlink';
          break;

        case 3:
          name += 'delivery';
          break;

        case 4:
          name += 'meet';
          break;

        case 5:
          name += 'counsel';
          break;

        case 8:
          name += 'commerce';
          break;

        case 10:
          name += 'reservation';
          break;

        case 11:
          name += 'booking';
          break;

        case 12:
          name += 'delivery';
          break;
      }

      return name;
    },
    btnStyle: function btnStyle() {
      var deco = {
        width: "".concat(this.width),
        height: "".concat(this.height),
        textAlign: 'center',
        lineHeight: "".concat(this.height),
        borderRadius: '8px',
        fontSize: '14px'
      };
      deco.backgroundColor = "".concat(this.getColor('Primary Color'));
      deco.color = 'white';
      return deco;
    }
  }
};
