import CardReviewTotalRegPc from "@/components/Components/Card/CardReviewTotalRegPc";
import CardReviewPc from "@/components/Components/Card/CardReviewPc";
import CardInquiryTotalRegPc from "@/components/Components/Card/CardInquiryTotalRegPc";
import CardInquiryPc from "@/components/Components/Card/CardInquiryPc";
import BlockMixin from "@/components/Mixins/BlockMixin";
export default {
  name: 'BlockProductDetailTabPc',
  components: {
    CardReviewTotalRegPc: CardReviewTotalRegPc,
    CardReviewPc: CardReviewPc,
    CardInquiryTotalRegPc: CardInquiryTotalRegPc,
    CardInquiryPc: CardInquiryPc
  },
  mixins: [BlockMixin],
  props: {
    fixedState: {
      type: Boolean,
      required: false
    }
  },
  data: function data() {
    return {
      "menu": [{
        "id": 0,
        "name": "소개"
      }, {
        "id": 1,
        "name": "리뷰",
        "count": 0
      }, {
        "id": 2,
        "name": "문의",
        "count": 0
      }],
      "detail": undefined,
      "imgStyle": {
        "width": "100%",
        "height": "240px",
        "objectFit": "cover"
      },
      "relatedList": [],
      "review_list_self": [],
      "inquiry_list_self": [],
      "review_list_other": [],
      "inquiry_list_other": []
    };
  },
  created: function created() {
    this.getData();
    this.getProductReview();
    this.getProductInquiry();
  },
  mounted: function mounted() {},
  methods: {
    getData: function getData() {
      var _this = this;

      this.$axios.get("/user/".concat(this.user.user_id, "/mapping/product/").concat(this.$route.query.id, "?fields=id,img,name,simple_desc,tags,form,\n      order_conversion_type,category1,category2,category3,rate,stock,input,like_count,reviews_count,visit,price,partner,\n      address,dong,distance,params,imgs,info,operating_time,phone,params,related_products")).then(function (res) {
        _this.detail = res.data;

        _this.getRelate();
      });
    },
    getRelate: function getRelate() {
      var _this2 = this;

      this.relatedList = [];

      if (this.detail.related_products && this.detail.related_products.length > 0) {
        var list = this.detail.related_products.slice(0, 4);
        list.forEach(function (item) {
          _this2.$axios.get("/user/".concat(_this2.user.user_id, "/mapping/product/").concat(item, "?fields=id,img,name,price")).then(function (res) {
            _this2.relatedList.push(res.data);
          });
        });
      }
    },
    clickCard: function clickCard(item) {
      this.$emit('changeToggle', this.toggle);
      var query = cloneItem(this.$route.query);
      query.id = item.id;
      this.$router.replace({
        path: this.$route.path,
        query: query
      });
    },
    getProductReview: function getProductReview() {
      var _this3 = this;

      this.review_list_self = [];
      this.review_list_other = [];
      var product_id = this.$route.query.id;
      this.url = "public/product/".concat(product_id, "/review?secret=true&page_num=1&page_length=8");
      if (this.isLogin) this.url = "user/".concat(this.user.user_id, "/product/").concat(product_id, "/review?secret=true&page_num=1&page_length=8");
      this.review_list_self = [];
      this.review_list_other = [];
      this.$axios.get(this.url).then(function (res) {
        res.data.data.forEach(function (item) {
          if (Number(item.author_id) === _this3.user.user_id) {
            item.self = true;

            _this3.review_list_self.push(item);
          } else {
            _this3.review_list_other.push(item);
          }
        });
        _this3.menu[1].count = res.data.count;
      });
    },
    getProductInquiry: function getProductInquiry() {
      var _this4 = this;

      this.inquiry_list_self = [];
      this.inquiry_list_other = [];
      var product_id = this.$route.query.id;
      this.url = "public/product/".concat(product_id, "/inquiry?secret=true&page_num=1&page_length=8");
      if (this.isLogin) this.url = "user/".concat(this.user.user_id, "/product/").concat(product_id, "/inquiry?secret=true&page_num=1&page_length=8");
      this.inquiry_list_self = [];
      this.inquiry_list_other = [];
      this.$axios.get(this.url).then(function (res) {
        res.data.data.forEach(function (item) {
          if (Number(item.author_id) === _this4.user.user_id) {
            item.self = true;

            _this4.inquiry_list_self.push(item);
          } else {
            _this4.inquiry_list_other.push(item);
          }
        });
        _this4.menu[2].count = res.data.count;
      });
    }
  },
  computed: {
    wrapStyle: function wrapStyle() {
      return {
        padding: '32px 0',
        borderBottom: "2px solid ".concat(this.getColor('보조 테두리선 색상'))
      };
    },
    selectedTabStyle: function selectedTabStyle() {
      return {
        color: "".concat(this.getColor('Primary Color')),
        padding: '14px 16px',
        fontSize: '14px',
        backgroundColor: 'white',
        marginRight: '8px',
        borderRadius: '8px'
      };
    },
    unselectedTabStyle: function unselectedTabStyle() {
      return {
        color: "".concat(this.getColor('기본 내용 색상')),
        padding: '14px 16px',
        fontSize: '14px',
        marginRight: '8px'
      };
    }
  },
  watch: {
    $route: function $route(n, o) {
      if (n.path === o.path) {
        this.getData();
      }
    }
  }
};
