import BtnPc from "@/components/Components/Button/BtnPc";
import BreadCrumbPc from "@/components/Components/Common/BreadCrumbPc";
import BlockProductDetailImages from "@/components/Components/Block/BlockProductDetailImages";
import BlockMixin from "@/components/Mixins/BlockMixin";
export default {
  name: 'BlockProductDetailCardPc',
  components: {
    BtnPc: BtnPc,
    BreadCrumbPc: BreadCrumbPc,
    BlockProductDetailImages: BlockProductDetailImages
  },
  mixins: [BlockMixin],
  props: {},
  data: function data() {
    return {
      "product": undefined,
      "breadcrumb": [],
      "fixedState": true,
      "imageStyle": {
        "width": "100%",
        "height": "460px",
        "objectFit": "cover"
      },
      "constructor": "",
      "layoutWidth": undefined
    };
  },
  created: function created() {
    this.getMaxWidth();
    this.getData(); // this.detectClientHeight();
  },
  mounted: function mounted() {// this.$nextTick(() => {
    //   window.addEventListener('scroll', this.detectClientHeight);
    // });
  },
  updated: function updated() {
    this.detectClientHeight();
  },
  methods: {
    getMaxWidth: function getMaxWidth() {
      var _this = this;

      this.$axios.get('public/ui/brand/admin').then(function (res) {
        _this.layoutWidth = res.data.layout_maxwidth;
      });
    },
    getData: function getData() {
      var _this2 = this;

      this.$axios.get("/user/".concat(this.user.user_id, "/mapping/product/").concat(this.$route.query.id, "?fields=id,img,name,simple_desc,tags,form,order_conversion_type,category1,category2,category3,rate,stock,input,like_count,reviews_count,visit,price,partner,address,dong,distance,params,imgs,info,operating_time,phone,params,location")).then(function (res) {
        _this2.product = res.data;
        _this2.product_params = _this2.cloneItem(_this2.product.params);

        _this2.setBreadCrumb();

        if (_this2.product.params && _this2.product.params.length > 0) {
          _this2.product.params.forEach(function (item) {
            if (item.param_id === 7 && item.value) {
              _this2.constructor = item.value;
            }
          });
        }
      });
    },
    detectClientHeight: function detectClientHeight() {
      this.$nextTick(function () {
        if (this.$refs.cardHeight) {
          if (document.documentElement.scrollTop > this.$refs.cardHeight.clientHeight) {
            this.fixedState = true;
            this.$emit('fixedState', true);
          } else {
            this.fixedState = false;
            this.$emit('fixedState', false);
          }
        }
      });
    },
    clickCall: function clickCall() {
      var _this3 = this;

      var user_id = this.$store.getters.user.user_id;
      var url = "user/".concat(user_id, "/product/").concat(this.$route.query.id, "/call");
      this.$axios.get(url).then(function (res) {
        location.href = "tel:".concat(res.data.call);

        _this3.$refs.callModal.close();
      });
    },
    setBreadCrumb: function setBreadCrumb() {
      this.breadcrumb.push({
        "link": "home",
        "name": "홈"
      });

      for (var i = 0; i < this.categories.length; i++) {
        this.categories[i].link = "product?tab=".concat(this.categories[i].id);

        if (this.product && this.product.category1 === this.categories[i].id) {
          this.breadcrumb.push({
            name: this.categories[i].name,
            link: this.categories[i].link,
            list: this.categories
          });
        }
      }

      this.breadcrumb.push({
        link: "product_detail?id=".concat(this.product.id),
        name: this.product.name
      }); // let catList = [];
      // this.category.forEach(cat => {
      //   let search = this.$route.query.search ? this.$$route.query.search : '';
      //   cat.link = `product?tab=${cat.id}&search=${search}`
      //   catList.push(cat);
      // })
      // let currentBread;
      // if (this.$route.query.tab===undefined || this.$route.query.tab==='') currentBread = catList[0];
      // else currentBread = find(catList, 'id', parseInt(this.$route.query.tab));
      // this.breadcrumb.push({
      //   name: currentBread.name,
      //   link: currentBread.link,
      //   list: catList
      // })
    },
    clickUp: function clickUp() {
      window.scrollTo({
        left: 0,
        top: 0,
        behavior: 'smooth'
      });
    },
    clickDown: function clickDown() {
      document.documentElement.scrollTop = document.body.scrollHeight;
    }
  },
  computed: {
    wrapStyle: function wrapStyle() {
      return {
        padding: '32px 0',
        borderBottom: "2px solid ".concat(this.getColor('보조 테두리선 색상'))
      };
    },
    productImgWidth: function productImgWidth() {
      return parseInt(this.pcMaxWidth.width, 10) * 66 / 100;
    },
    imgStyle: function imgStyle() {
      return {
        width: '100%',
        objectFit: 'cover'
      };
    },
    partnerImg: function partnerImg() {
      return {
        width: '48px',
        height: '48px',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        backgroundImage: "url(\"".concat(this.product.partner.img, "\")"),
        borderRadius: '50%'
      };
    },
    categories: function categories() {
      return this.$store.getters.category;
    },
    iconWrapStyle: function iconWrapStyle() {
      return {
        width: '32px',
        height: '32px',
        backgroundColor: 'white',
        borderRadius: '4px',
        boxShadow: '0 2px 8px 0 rgba(0,0,0,0.1)'
      };
    }
  },
  watch: {
    $route: function $route(n, o) {
      if (n.path === o.path) {
        this.getData();
      }
    }
  }
};
