import CardProductReviewPc from "@/components/Components/Card/CardProductReviewPc";
import BreadCrumbPc from "@/components/Components/Common/BreadCrumbPc";
import BlockMixin from "@/components/Mixins/BlockMixin";
import FormMixin from "@/components/Mixins/FormMixin";
import HeaderMixin from "@/components/Mixins/HeaderMixin";
import FormCustomMixin from "@/components/Components/Mixin/FormCustomMixin";
export default {
  name: 'BlockReviewRegPc',
  components: {
    CardProductReviewPc: CardProductReviewPc,
    BreadCrumbPc: BreadCrumbPc
  },
  mixins: [BlockMixin, FormMixin, HeaderMixin, FormCustomMixin],
  props: {},
  data: function data() {
    return {
      "vBread": false,
      "formData": [{
        "name": "평점",
        "field": "rate",
        "label": "평점",
        "value": 5,
        "format": "CRating",
        "validate": {
          "required": false,
          "valid_required": false
        }
      }, {
        "name": "제목",
        "field": "title",
        "label": "제목",
        "value": "",
        "format": "CInput_text",
        "validate": {
          "num": true,
          "alpha_g": true,
          "alpha_s": true,
          "special": true,
          "required": true,
          "max_length": 30,
          "min_length": 1,
          "valid_required": true
        },
        "placeholder": "제목을 입력하세요"
      }, {
        "name": "내용",
        "field": "content",
        "label": "내용",
        "value": "",
        "format": "CInput_textarea",
        "validate": {
          "num": true,
          "alpha_g": true,
          "alpha_s": true,
          "special": true,
          "required": true,
          "max_length": 500,
          "min_length": 1,
          "valid_required": true
        },
        "placeholder": "내용을 입력하세요"
      }, {
        "name": "비밀글",
        "field": "is_secret",
        "label": "비밀글로 작성합니다.",
        "value": false,
        "format": "CInput_checkbox",
        "validate": {
          "num": true,
          "alpha_g": true,
          "alpha_s": true,
          "special": true,
          "required": false,
          "valid_required": false
        },
        "placeholder": ""
      }, {
        "name": "첨부파일 추가",
        "field": "imgs",
        "label": "",
        "format": "CFileUploaderSingle",
        "validate": {
          "required": false,
          "valid_required": false
        },
        "placeholder": "+첨부파일 추가"
      }],
      "breadcrumbs": undefined,
      "list_product": []
    };
  },
  created: function created() {
    this.review_id = this.$route.query.review_id;
    this.product_id = this.$route.query.product_id;
    this.getReviewDetail();
    this.getProductDetail();
    this.initForm();
  },
  methods: {
    setBreadCrumb: function setBreadCrumb() {
      this.breadcrumbs = [];
      this.breadcrumbs.push({
        "link": "home",
        "name": "홈"
      });

      for (var i = 0; i < this.categories.length; i++) {
        this.categories[i].link = "product?tab=".concat(this.categories[i].id);

        if (this.list_product[0].category1 === this.categories[i].id) {
          this.breadcrumbs.push({
            name: this.categories[i].name,
            link: this.categories[i].link,
            list: this.categories
          });
        }
      }

      this.breadcrumbs.push({
        name: this.list_product[0].name,
        link: "product_detail?id=".concat(this.list_product[0].id)
      });
      this.breadcrumbs.push({
        name: '리뷰 작성'
      });
      this.vBread = true;
    },
    initForm: function initForm() {
      this.formData = this.setForm(this.formData);
      var title = find(this.formData, 'field', 'title');
      title.inputCustom = this.inputCustom;
      var content = find(this.formData, 'field', 'content');
      content.inputCustom = this.inputCustomTextarea;
    },
    getProductDetail: function getProductDetail() {
      var _this = this;

      this.list_product = [];
      var url = "user/".concat(this.user.user_id, "/mapping/product/").concat(this.product_id, "?fields=");
      this.$axios.get(url).then(function (res) {
        _this.list_product.push(res.data);

        _this.setBreadCrumb();
      });
    },
    save: function save() {
      var _this2 = this;

      var result = this.getParamData(this.formData); // 주문상품의 후기일 경우

      if (this.$route.query.order_product !== undefined) result.params.order_product_id = this.$route.query.order_product;

      if (result.enable) {
        if (this.review_id === undefined) {
          console.log('200');
          this.$axios.post("user/".concat(this.user.user_id, "/product/").concat(this.product_id, "/review"), result.params).then(function (res) {
            if (res.status === 200) {
              _this2.toast('후기글이 작성되었습니다.');

              if (_this2.$route.query && _this2.$route.query.partner_id !== undefined && _this2.$route.query.partner_id !== 'null') {
                _this2.$router.replace("/product_detail?id=".concat(_this2.$route.query.partner_id, "&tab=1"));
              } else _this2.$router.replace("/product_detail?id=".concat(_this2.product_id, "&tab=1"));
            }
          }).catch(function (err) {
            if (!err.response.data.message) _this2.toast(err.response.data.detail);else _this2.toast(err.response.data.message);
          });
        } else {
          this.$axios.patch("user/".concat(this.user.user_id, "/product/").concat(this.product_id, "/review/").concat(this.review_id), result.params).then(function (res) {
            if (res.status === 200) {
              _this2.toast('후기글이 수정되었습니다.');

              if (_this2.$route.query && _this2.$route.query.partner_id !== undefined && _this2.$route.query.partner_id !== 'null') {
                _this2.$router.replace("/product_detail?id=".concat(_this2.$route.query.partner_id, "&tab=1"));
              } // if (this.$route.query&&this.$route.query.product_id) {
              //   this.$router.replace(`/product_detail?id=${this.$route.query.product_id}&tab=1`);
              // }
              else _this2.$router.replace("/product_detail?id=".concat(_this2.product_id, "&tab=1"));
            }
          }).catch(function (err) {
            if (!err.response.data.message) _this2.toast(err.response.data.detail);else _this2.toast(err.response.data.message);
          });
        }
      }
    },
    getReviewDetail: function getReviewDetail() {
      var _this3 = this;

      if (this.review_id !== undefined) {
        this.$axios.get("user/".concat(this.user.user_id, "/product/").concat(this.product_id, "/review/").concat(this.review_id)).then(function (res) {
          var data = res.data;

          _this3.formData.forEach(function (item) {
            item.value = data[item.field];
          });
        });
      }
    }
  },
  computed: {
    buttonCustomCancel: function buttonCustomCancel() {
      var styles = this.cloneItem(this.buttonCustom);
      styles.backgroundColor = this.brandColor.sub.color;
      styles.width = '192px';
      return styles;
    },
    buttonCustomPrimary: function buttonCustomPrimary() {
      var styles = this.cloneItem(this.buttonCustom);
      styles.backgroundColor = this.brandColor.primary.color;
      styles.width = '192px';
      return styles;
    },
    inputCustomTextarea: function inputCustomTextarea() {
      var styles = this.cloneItem(this.inputCustom);
      styles.height = '160px';
      return styles;
    },
    categories: function categories() {
      return this.$store.getters.category;
    }
  },
  watch: {}
};
