import CardReviewTotalReg from "@/components/Components/Card/CardReviewTotalReg";
import CardReview from "@/components/Components/Card/CardReview";
import CardInquiryTotalReg from "@/components/Components/Card/CardInquiryTotalReg";
import CardInquiry from "@/components/Components/Card/CardInquiry";
import UpdownScroll from "@/components/Components/Button/UpdownScroll";
export default {
  name: 'BlockProductDetailTab',
  components: {
    CardReviewTotalReg: CardReviewTotalReg,
    CardReview: CardReview,
    CardInquiryTotalReg: CardInquiryTotalReg,
    CardInquiry: CardInquiry,
    UpdownScroll: UpdownScroll
  },
  mixins: [],
  props: {
    fixedState: {
      type: Boolean,
      required: false
    }
  },
  data: function data() {
    return {
      "menu": [{
        "id": 0,
        "name": "소개"
      }, {
        "id": 1,
        "name": "리뷰",
        "count": 0
      }, {
        "id": 2,
        "name": "문의",
        "count": 0
      }],
      "detail": undefined,
      "imgStyle": {
        "width": "100%",
        "height": "160px",
        "objectFit": "cover"
      },
      "relatedList": [],
      "review_list_self": [],
      "inquiry_list_self": [],
      "review_list_other": [],
      "inquiry_list_other": []
    };
  },
  created: function created() {
    this.getData();
    this.getProductReview();
    this.getProductInquiry();
  },
  mounted: function mounted() {},
  methods: {
    clickUp: function clickUp() {
      window.scrollTo({
        left: 0,
        top: 0,
        behavior: 'smooth'
      });
    },
    clickDown: function clickDown() {
      document.documentElement.scrollTop = document.body.scrollHeight;
    },
    getData: function getData() {
      var _this = this;

      this.$axios.get("/user/".concat(this.user.user_id, "/mapping/product/").concat(this.$route.query.id, "?fields=id,img,name,simple_desc,tags,form,order_conversion_type,category1,category2,category3,rate,stock,input,like_count,reviews_count,visit,price,partner,address,dong,distance,params,imgs,info,operating_time,phone,params,location,related_products")).then(function (res) {
        res.data.info = res.data.info.replace(/<img /gi, '<img style="margin:0 -16px;max-width:100vw" ');
        _this.detail = res.data;

        _this.getRelate();
      });
    },
    getRelate: function getRelate() {
      var _this2 = this;

      if (this.detail.related_products && this.detail.related_products.length > 0) {
        var list = this.detail.related_products.slice(0, 2);
        list.forEach(function (item) {
          _this2.$axios.get("/user/".concat(_this2.user.user_id, "/mapping/product/").concat(item, "?fields=id,img,name,price")).then(function (res) {
            _this2.relatedList.push(res.data);
          });
        });
      }
    },
    clickCard: function clickCard(item) {
      this.$emit('changeToggle', this.toggle);
      var query = cloneItem(this.$route.query);
      query.id = item.id;
      this.$router.replace({
        path: this.$route.path,
        query: query
      });
    },
    getProductReview: function getProductReview() {
      var _this3 = this;

      this.review_list_self = [];
      this.review_list_other = [];
      var product_id = this.$route.query.id;
      this.url = "public/product/".concat(product_id, "/review?secret=true&page_num=1&page_length=8");
      if (this.isLogin) this.url = "user/".concat(this.user.user_id, "/product/").concat(product_id, "/review?secret=true&page_num=1&page_length=8");
      this.$axios.get(this.url).then(function (res) {
        res.data.data.forEach(function (item) {
          if (Number(item.author_id) === _this3.user.user_id) {
            item.self = true;

            _this3.review_list_self.push(item);
          } else {
            _this3.review_list_other.push(item);
          }
        });
        _this3.menu[1].count = res.data.count;
      });
    },
    getProductInquiry: function getProductInquiry() {
      var _this4 = this;

      this.inquiry_list_self = [];
      this.inquiry_list_other = [];
      var product_id = this.$route.query.id;
      this.url = "public/product/".concat(product_id, "/inquiry?secret=true&page_num=1&page_length=8");
      if (this.isLogin) this.url = "user/".concat(this.user.user_id, "/product/").concat(product_id, "/inquiry?secret=true&page_num=1&page_length=8");
      this.$axios.get(this.url).then(function (res) {
        res.data.data.forEach(function (item) {
          if (Number(item.author_id) === _this4.user.user_id) {
            item.self = true;

            _this4.inquiry_list_self.push(item);
          } else {
            _this4.inquiry_list_other.push(item);
          }
        });
        _this4.menu[2].count = res.data.count;
      });
    },
    delReview: function delReview() {
      this.getProductReview();
    },
    delInquiry: function delInquiry() {
      this.getProductInquiry();
    }
  },
  computed: {
    scrollStyle: function scrollStyle() {
      return {
        bottom: '148px',
        right: '24px'
      };
    },
    marginStyle: function marginStyle() {
      return {
        padding: '16px 0 100px'
      };
    },
    normalStyle: function normalStyle() {
      return {
        padding: '16px 0 100px'
      };
    },
    selectedTabStyle: function selectedTabStyle() {
      return {
        color: "".concat(this.getColor('Primary Color')),
        width: '114px',
        height: '40px',
        lineHeight: '40px',
        textAlign: 'center',
        fontSize: '14px',
        backgroundColor: 'white'
      };
    },
    unselectedTabStyle: function unselectedTabStyle() {
      return {
        color: "".concat(this.getColor('기본 내용 색상')),
        width: '114px',
        height: '40px',
        lineHeight: '40px',
        textAlign: 'center',
        fontSize: '14px',
        backgroundColor: "rgba(0, 0, 0, 0.05)"
      };
    },
    iconWrapStyle: function iconWrapStyle() {
      return {
        width: '32px',
        height: '32px',
        backgroundColor: 'white',
        borderRadius: '4px',
        boxShadow: '0 4px 24px 0 rgba(0, 0, 0, 0.24)'
      };
    }
  },
  watch: {}
};
