import Slick from 'vue-slick'

export default {
  name: 'image-slider-arrow',

  components: {
    Slick
  },

  props: {
    arrow: {
      type: Boolean,
      default: false
    },
    indicator: {
      type: Boolean,
      default: false
    },
    listData: {
      type: Array,
      required: true
    },
    height: {
      type: Number,
      required: false
    },
    autoplay: {
      type: Boolean,
      default: false
    },
    slidesToShow: {
      type: Number,
      default: 1
    },
    slidesToScroll: {
      type: Number,
      default: 1
    },
    autoplaySpeed: {
      type: Number,
      default: 3000
    },
    containerWidth: {
      type: Number,
      default: 900
    },
    contentWidth: {
      type: Number,
      default: 800
    },
    arrowLeftStyle: {
      type: Object,
      required: false
    },
    arrowRightStyle: {
      type: Object,
      required: false
    },
    slideCardStyle: {
      type: Object,
      required: false
    },
    imageStyle: {
      type: Object,
      required: false
    },
  },

  data() {
    return {
      currentSlide: 0,
      reRender: true
    }
  },

  mounted() {
    this.$nextTick(() => {
      this.reRenderSlick();
    });
  },

  methods: {
    reRenderSlick() {
      if(this.reRender === false) return;

      this.$nextTick(() => {
        this.reRender = false;
        setTimeout(() => {
          this.reRender = true;
        },500);
      });
    },

    getHref(link) {
      location.target = (link.indexOf('http:')>-1 || link.indexOf('https:')>-1) ? '_blank' : '';
      location.href = link;
    },

    getTarget(link) {
      return (link.indexOf('http:')>-1 || link.indexOf('https:')>-1) ? '_blank' : '';
    },

    handleAfterChange(event, slick, currentSlide) {
      this.currentSlide = currentSlide;
    },

    imgStyle(img) {
      let height = '60px';
      if(this.height)
        height = this.height + 'px';

      return {
        width: '100%',
        height: height,
        backgroundImage: this.imageStyle&&this.imageStyle.backgroundImage ?
          this.imageStyle.backgroundImage : `url(${img.url})`,
        backgroundPosition: this.imageStyle&&this.imageStyle.backgroundPosition ?
          this.imageStyle.backgroundPosition : 'center',
        backgroundSize: this.imageStyle&&this.imageStyle.backgroundSize ?
          this.imageStyle.backgroundSize : 'cover',
        backgroundRepeat: this.imageStyle&&this.imageStyle.backgroundRepeat ?
          this.imageStyle.backgroundRepeat : 'no-repeat',
        marginBottom: '-5px'
      }
    },
  },

  computed: {

    arrowLeftComputedStyle() {
      if (this.arrowLeftStyle) return this.arrowLeftStyle;
      else {
        return {
          fontSize: '40px',
          top: 'calc(50% - 20px)',
          left: 0
        }
      }
    },

    arrowRightComputedStyle() {
      if (this.arrowRightStyle) return this.arrowRightStyle;
      else {
        return {
          fontSize: '40px',
          top: 'calc(50% - 20px)',
          right: 0
        }
      }
    },

    currentIndicatorStyle() {
      return {
        width: '8px',
        height: '8px',
        borderRadius: '100%',
        backgroundColor: 'rgba(255, 255, 255, 1)',
        marginRight: '4px'
      };
    },

    indicatorStyle() {
      return {
        width: '8px',
        height: '8px',
        borderRadius: '100%',
        backgroundColor: 'rgba(255, 255, 255, 0.6)',
        marginRight: '4px'
      };
    },

    // 슬릭 옵션
    slickOptions() {

      return {
        infinite: true,
        variableWidth: false,
        centerPadding: '',
        centerMode: false,
        autoplay: this.autoplay,
        arrows: false,
        dots: false,
        slidesToShow: this.slidesToShow,
        slidesToScroll: this.slidesToScroll,
        autoplaySpeed: this.autoplaySpeed,
      }
    }
  }
}
